mapper.select:
        SELECT

        FROM

        WHERE 1=1

                AND DATASOURCE_NAME

                    ${variable.operators.datasourceName}
                     = 

				#{variable.datasourceName}


                AND DATASOURCE_TYPE

                    ${variable.operators.datasourceType}
                     = 

				#{variable.datasourceType}


                AND DATASOURCE_SCHEMA

                    ${variable.operators.datasourceSchema}
                     = 

				#{variable.datasourceSchema}


                AND DATASOURCE_DRIVER

                    ${variable.operators.datasourceDriver}
                     = 

				#{variable.datasourceDriver}


                AND DATASOURCE_URL

                    ${variable.operators.datasourceUrl}
                     = 

				#{variable.datasourceUrl}


                AND DATASOURCE_USER

                    ${variable.operators.datasourceUser}
                     = 

				#{variable.datasourceUser}


                AND DATASOURCE_PASSWORD

                    ${variable.operators.datasourcePassword}
                     = 

				#{variable.datasourcePassword}


                AND DATASOURCE_DESC

                    ${variable.operators.datasourceDesc}
                     = 

				#{variable.datasourceDesc}


                AND CRT_USER_CODE

                    ${variable.operators.crtUserCode}
                     = 

				#{variable.crtUserCode}


                AND CRT_ORG_CODE

                    ${variable.operators.crtOrgCode}
                     = 

				#{variable.crtOrgCode}


                AND CRT_DATE

                    ${variable.operators.crtDate}
                     = 

				#{variable.crtDate}


                AND UPD_USER_CODE

                    ${variable.operators.updUserCode}
                     = 

				#{variable.updUserCode}


                AND UPD_ORG_CODE

                    ${variable.operators.updOrgCode}
                     = 

				#{variable.updOrgCode}


                AND UPD_DATE

                    ${variable.operators.updDate}
                     = 

				#{variable.updDate}


                AND ID

                    ${variable.operators.id}
                     = 

				#{variable.id}
