mapper.select:
        SELECT

        FROM

        WHERE 1=1

                AND ID

                    ${variable.operators.id}
                     = 

				#{variable.id}


                AND DIM_CODE

                    ${variable.operators.dimCode}
                     = 

				#{variable.dimCode}


                AND DATASOURCE_ID

                    ${variable.operators.datasourceId}
                     = 

				#{variable.datasourceId}


                AND DIM_NAME

                    ${variable.operators.dimName}
                     = 

				#{variable.dimName}


                AND TABLE_NAME

                    ${variable.operators.tableName}
                     = 

				#{variable.tableName}


                AND NAME_COLUMN

                    ${variable.operators.nameColumn}
                     = 

				#{variable.nameColumn}


                AND NAME_COLUMN_DESC

                    ${variable.operators.nameColumnDesc}
                     = 

				#{variable.nameColumnDesc}


                AND CODE_COLUMN

                    ${variable.operators.codeColumn}
                     = 

				#{variable.codeColumn}


                AND CODE_COLUMN_DESC

                    ${variable.operators.codeColumnDesc}
                     = 

				#{variable.codeColumnDesc}


                AND PID

                    ${variable.operators.pid}
                     = 

				#{variable.pid}


                AND LEVEL

                    ${variable.operators.level}
                     = 

				#{variable.level}


                AND ALIAS

                    ${variable.operators.alias}
                     = 

				#{variable.alias}


                AND PK_FLAG

                    ${variable.operators.pkFlag}
                     = 

				#{variable.pkFlag}


                AND CRT_USER_CODE

                    ${variable.operators.crtUserCode}
                     = 

				#{variable.crtUserCode}


                AND CRT_ORG_CODE

                    ${variable.operators.crtOrgCode}
                     = 

				#{variable.crtOrgCode}


                AND CRT_DATE

                    ${variable.operators.crtDate}
                     = 

				#{variable.crtDate}


                AND UPD_USER_CODE

                    ${variable.operators.updUserCode}
                     = 

				#{variable.updUserCode}


                AND UPD_ORG_CODE

                    ${variable.operators.updOrgCode}
                     = 

				#{variable.updOrgCode}


                AND UPD_DATE

                    ${variable.operators.updDate}
                     = 

				#{variable.updDate}
