mapper.select:
        SELECT

        FROM

        WHERE 1=1

                AND ID

                    ${variable.operators.id}
                     = 

				#{variable.id}


                AND IE_CODE

                    ${variable.operators.ieCode}
                     = 

				#{variable.ieCode}


                AND IE_NAME

                    ${variable.operators.ieName}
                     = 

				#{variable.ieName}


                AND CATEGORY_ID

                    ${variable.operators.categoryId}
                     = 

				#{variable.categoryId}


                AND IE_PROP

                    ${variable.operators.ieProp}
                     = 

				#{variable.ieProp}


                AND IE_TYPE

                    ${variable.operators.ieType}
                     = 

				#{variable.ieType}


                AND IE_DESC

                    ${variable.operators.ieDesc}
                     = 

				#{variable.ieDesc}


                AND IE_RULE

                    ${variable.operators.ieRule}
                     = 

				#{variable.ieRule}


                AND IE_DEFAULT_VALUE

                    ${variable.operators.ieDefaultValue}
                     = 

				#{variable.ieDefaultValue}


                AND IE_METHOD

                    ${variable.operators.ieMethod}
                     = 

				#{variable.ieMethod}


                AND CALC_FREQ

                    ${variable.operators.calcFreq}
                     = 

				#{variable.calcFreq}


                AND STATUS

                    ${variable.operators.status}
                     = 

				#{variable.status}


                AND IE_DATA_UNIT

                    ${variable.operators.ieDataUnit}
                     = 

				#{variable.ieDataUnit}


                AND IE_LEVEL

                    ${variable.operators.ieLevel}
                     = 

				#{variable.ieLevel}


                AND START_DATE

                    ${variable.operators.startDate}
                     = 

				#{variable.startDate}


                AND END_DATE

                    ${variable.operators.endDate}
                     = 

				#{variable.endDate}


                AND RETENTION

                    ${variable.operators.retention}
                     = 

				#{variable.retention}


                AND PROCESS_INSTANCE_ID

                    ${variable.operators.processInstanceId}
                     = 

				#{variable.processInstanceId}


                AND MANAGE_DEPT

                    ${variable.operators.manageDept}
                     = 

				#{variable.manageDept}


                AND CRT_USER_CODE

                    ${variable.operators.crtUserCode}
                     = 

				#{variable.crtUserCode}


                AND CRT_ORG_CODE

                    ${variable.operators.crtOrgCode}
                     = 

				#{variable.crtOrgCode}


                AND CRT_DATE

                    ${variable.operators.crtDate}
                     = 

				#{variable.crtDate}


                AND UPD_USER_CODE

                    ${variable.operators.updUserCode}
                     = 

				#{variable.updUserCode}


                AND UPD_ORG_CODE

                    ${variable.operators.updOrgCode}
                     = 

				#{variable.updOrgCode}


                AND UPD_DATE

                    ${variable.operators.updDate}
                     = 

				#{variable.updDate}
