mapper.select:
        SELECT

        FROM

        WHERE 1=1

                AND ID

                    ${variable.operators.id}
                     = 

				#{variable.id}


                AND CATEGORY_ID

                    ${variable.operators.categoryId}
                     = 

				#{variable.categoryId}


                AND PARM_CODE

                    ${variable.operators.parmCode}
                     like 

                CONCAT('%',#{variable.parmCode},'%')


                AND PARM_NAME

                    ${variable.operators.parmName}
                     like 

                CONCAT('%',#{variable.parmName},'%')


                AND PARM_TYPE

                    ${variable.operators.parmType}
                     = 

				#{variable.parmType}


                AND PARM_VALUE

                    ${variable.operators.parmValue}
                     = 

				#{variable.parmValue}


                AND PARM_DESC

                    ${variable.operators.parmDesc}
                     = 

				#{variable.parmDesc}


                AND STATUS

                    ${variable.operators.status}
                     = 

				#{variable.status}


                AND CRT_USER_CODE

                    ${variable.operators.crtUserCode}
                     = 

				#{variable.crtUserCode}


                AND CRT_ORG_CODE

                    ${variable.operators.crtOrgCode}
                     = 

				#{variable.crtOrgCode}


                AND CRT_DATE

                    ${variable.operators.crtDate}
                     = 

				#{variable.crtDate}


                AND UPD_USER_CODE

                    ${variable.operators.updUserCode}
                     = 

				#{variable.updUserCode}


                AND UPD_ORG_CODE

                    ${variable.operators.updOrgCode}
                     = 

				#{variable.updOrgCode}


                AND UPD_DATE

                    ${variable.operators.updDate}
                     = 

				#{variable.updDate}
